if (!flow.get('sendingDataSettings')) return null;
info = msg.payload;
var scopeLimits = flow.get('scopeLimits')
if (scopeLimits.npts < 0) 
{
    var info1 = info.split(',');
    scopeLimits.xstart = Number(info1[0]);
    scopeLimits.xstop = Number(info1[1]);
    scopeLimits.npts = Number(info1[2]);
    flow.set('sendingDataSettings', false)
    flow.set('scopeLimits', scopeLimits);
}
var newMsg = 
{
