}
return newMsg;

if (!flow.get('waitingForData')) return [null, null, null, null];
if (msg.payload.length == 1)
{
    var msg1 = {'topic':'getChan1','payload':'CHAN1:DATA?\n'};
    var msg2 = {'topic':'getChan2','payload':'CHAN2:DATA?\n'};
    var msg3 = {'topic':'getChan3','payload':'CHAN3:DATA?\n'};
    var msg4 = {'topic':'getChan4','payload':'CHAN4:DATA?\n'};
    context.set('itrace',1);
    return [msg1, msg2, msg3, msg4, null];
}
else
{

    if (context.get('itrace') == 1)
    {
        context.set('trace1',msg.payload);
        context.set('itrace',2);
        return [null, null, null, null, null];
    }

    if (context.get('itrace') == 2)
    {
        context.set('trace2',msg.payload);
        context.set('itrace',3);
        return [null, null, null, null, null];
    }

    if (context.get('itrace') == 3)
    {
        context.set('trace3',msg.payload);
        context.set('itrace',4);
        return [null, null, null, null, null];
    }

    if (context.get('itrace') == 4)
    {
        var rfFreq = flow.get('setupData').rfFreq;
        context.set('itrace',1);
        context.set('trace4',msg.payload);
        var scopeLimits = flow.get('scopeLimits');
        var nptsRev = Math.round(scopeLimits.npts * 176.0e-6 / rfFreq / (scopeLimits.xstop - scopeLimits.xstart));
        var istart0 = Math.round ((scopeLimits.npts - nptsRev) / 2);
        istart0 = flow.get('setupData').iptStart;
        var trace1String = context.get('trace1').split(',');
        var trace2String = context.get('trace2').split(',');
        var trace3String = context.get('trace3').split(',');
        var trace4String = context.get('trace4').split(',');

        var trace1 = [];
        var trace2 = [];
        var trace3 = [];
        var trace4 = [];
        var nptPerBunch = nptsRev / 176.0;
        var twoPi = 2.0 *3.1415927;

        for (var ii = 0; ii < 176; ++ii) 
        {
            var istart = Math.round(ii * nptPerBunch) + istart0;
            var istop  = istart + Math.round(nptPerBunch);
            trace1[ii] = 0.0;
            trace2[ii] = 0.0;
            trace3[ii] = 0.0;
            trace4[ii] = 0.0;
            for (var ipt = istart; ipt <= istop; ++ipt) 
            {
                trace1[ii] = trace1[ii] + Number(trace1String[ipt]);
                trace2[ii] = trace2[ii] + Number(trace2String[ipt]);
                trace3[ii] = trace3[ii] + Number(trace3String[ipt]);
                trace4[ii] = trace4[ii] + Number(trace4String[ipt]);
            }
            trace1[ii] = trace1[ii] / nptPerBunch;
            trace2[ii] = trace2[ii] / nptPerBunch;
            trace3[ii] = trace3[ii] / nptPerBunch;
            trace4[ii] = trace4[ii] / nptPerBunch;
        }
        var msg5 = 
        {
            'topic':'OscillationOverthruster/data/traces',
            'payload':
            {
                'trace1'  :   trace1,
                'trace2'  :   trace2,
                'trace3'  :   trace3,
                'trace4'  :   trace4
            }
        };
        flow.set('waitingForData', false);
        return [null, null, null, null, msg5];
    }
}

var rfFreq = flow.get('setupData').rfFreq;
var bunchStart = flow.get('setupData').bunchStart;
var bunchStop = flow.get('setupData').bunchStop;
var magn = [];
var phase = [];

var trace1 = msg.payload.trace1;
var trace2 = msg.payload.trace2;
var trace3 = msg.payload.trace3;
var trace4 = msg.payload.trace4;

var twoPi = 2.0 *3.1415927;
var avgMag = 0.0;
var bunchLengthScale = [];
var peakBeamCurrent = [];
var v5overV1 = 5.0; // for bunch length calibration)

for (var ii = 0; ii < 176; ++ii) 
{
    phase[ii] = 0.0;
    magn[ii] = Math.sqrt(trace1[ii] * trace1[ii] + trace2[ii] * trace2[ii]);
    bunchLengthScale[ii] = 0.0;

    var v500 = -0.15674603 + Math.sqrt(0.15674603 * 0.15674603 -(-16.1 - trace4[ii] * 1000.0)/126);
    var v100 =  1.24705882 - Math.sqrt(1.24705882 * 1.24705882 +(-3.16 - magn[ii] * 1000.0)/170);
    if (v500 < 0.001) v500 = 0.001;
    if (v100 < 0.001) v100 = 0.001;
    if ((bunchStart <= ii) && (ii <= bunchStop))
    {
// added factor of two for 6 dB splitter
// added another factor of 2 to match call 2018-10-09

        phase[ii] = (1000000.0 / rfFreq) * Math.atan(2.0 * trace2[ii] /trace1[ii]) / (4.0 * 3.1415927);
        bunchLengthScale[ii] = v5overV1 * v100 / v500;
        if (bunchLengthScale[ii] < 1.0) bunchLengthScale[ii] = 1.0;
        bunchLengthScale[ii] = Math.sqrt(Math.log(bunchLengthScale[ii]) / 24.0);
        bunchLengthScale[ii] = 1000.0 * 1000.0 * 2.0 * bunchLengthScale[ii] /(rfFreq * twoPi * 1.414); //ps
    }
// cal'd on 2019-05-08 
    magn[ii] = (2517.7 * magn[ii]  - 0.0) / 176.0;
    avgMag = avgMag + magn[ii];
}
for (var ii = 0; ii < bunchStart; ++ii) 
{
    bunchLengthScale[ii] = bunchLengthScale[bunchStart];
}
for (var ii = bunchStop; ii < 176; ++ii) 
{
    bunchLengthScale[ii] = bunchLengthScale[bunchStop];
}
for (var ii = 0; ii < 176; ++ii) 
{
    peakBeamCurrent[ii] = 1000.0 * 1000.0 * magn[ii] / (rfFreq * bunchLengthScale[ii] * 2.506);

}
avgMag = avgMag /176.0;
var newMsg = 
{
    'topic'   : 'bunchWaveforms',
    'payload' :
    {
        'trace1'          :   trace1,
        'trace2'          :   trace2,
        'trace3'          :   trace3,
        'trace4'          :   trace4,
        'magn'            :   magn,
        'phase'           :   phase,
        'avgMag'          :   avgMag,
        'bunchLengthScale':   bunchLengthScale,
        'peakBeamCurrent' :   peakBeamCurrent
    }
};
return newMsg;

var difPhase = [];
var phaseHistory = [[],[],[]];

var twoPi = 2.0 *3.1415927;

var lastTrace = context.get('lastTrace');
if (lastTrace == null)
{
    phaseHistory[0] = msg.payload.phase;
    for (var itrace = 1; itrace < 3; ++itrace)
    {
        for (var ii = 0; ii < 176; ++ii)
        {
            phaseHistory[itrace][ii] = 0.0;
        }
    }
    context.set('phaseHistory',phaseHistory);
    context.set('lastTrace',0);
    for (var ii = 0; ii < 176; ++ii)
    {
        difPhase[ii] = 0.0;
    }
}
else
{
    phaseHistory = context.get('phaseHistory');
    var nextTrace = lastTrace + 1;
    if (nextTrace > 2) nextTrace = 0;
    phaseHistory[nextTrace] = msg.payload.phase;
    context.set('phaseHistory',phaseHistory);
    context.set ('lastTrace',nextTrace);
    for (var ii = 0; ii < 176; ++ii)
    {
        difPhase[ii] = phaseHistory[nextTrace][ii] - phaseHistory[lastTrace][ii];
    }
}
var newMsg = 
{
    'topic'   : 'bunchWaveforms',
    'payload' :
    {
        'trace1'            :   msg.payload.trace1,
        'trace2'            :   msg.payload.trace2,
        'trace3'            :   msg.payload.trace3,
        'trace4'            :   msg.payload.trace4,
        'magn'              :   msg.payload.magn,
        'phase'             :   msg.payload.phase,
        'avgMag'            :   msg.payload.avgMag,
        'bunchLengthScale'  :   msg.payload.bunchLengthScale,
        'peakBeamCurrent'   :   msg.payload.peakBeamCurrent,
        'difPhase'          :   difPhase,

    }
};
return newMsg

function getAttrObject(device, propName, attrName)
{
    var props = device[propName];
    var attr = props.find(attr => attr.attr === attrName);
    return attr;
}

var bunchStart = flow.get('setupData').bunchStart;
var bunchStop = flow.get('setupData').bunchStop;
var twoPi = 2.0 *3.1415927;
var modeM = [];
var magThreshold = 0.5 * msg.payload.avgMag;
var nbunches = bunchStop - bunchStart + 1.0;
for (var mode = 0; mode < 88; ++mode)
{
    var modeI = 0.0;
    var modeQ = 0.0;
    for (var bunch = bunchStart; bunch <= bunchStop; ++bunch)
    {
        modeI = modeI + Math.cos(twoPi * mode  * bunch / 176.0 ) * msg.payload.difPhase[bunch];
        modeQ = modeQ + Math.sin(twoPi * mode  * bunch / 176.0 ) * msg.payload.difPhase[bunch];
    }
    modeI = modeI / nbunches;
    modeQ = modeQ / nbunches;
    modeM[mode] = Math.sqrt(modeI * modeI + modeQ * modeQ);
}

var bunchCount = 0.0;
var beamCurrent = 0.0;
var avgPhase = 0.0;
var avgQ = 0.0;
for (var ii = 0; ii < 176; ++ii)
{
    beamCurrent = beamCurrent + msg.payload.magn[ii];
    if (msg.payload.magn[ii] > magThreshold)
    {
        avgPhase = avgPhase + msg.payload.phase[ii];
        bunchCount = bunchCount + 1;
    }
    avgQ = avgQ + msg.payload.trace3[ii];
}
if (bunchCount < 1.0) bunchCount = 1.0;
beamCurrent = Math.round(100.0 * beamCurrent) / 100.0;
avgPhase = Math.round(100.0 * avgPhase / bunchCount) / 100.0;
avgQ = avgQ / 176.0;
avgQ = Math.round(10000.0 * avgQ) / 10000.0;

var maxDipoleMode = -1
var maxDipoleModeAmp = 0.0;
var rmsModeAmp = 0.0;
for (var ii = 0; ii < 88; ++ii)
{
    rmsModeAmp = rmsModeAmp + modeM[ii] * modeM[ii];
    if (modeM[ii] > maxDipoleModeAmp)
    {
        maxDipoleModeAmp = modeM[ii];
        maxDipoleMode = ii;
    }
}
maxDipoleModeAmp = Math.round(100.0 * maxDipoleModeAmp) / 100.0;
rmsModeAmp = Math.round(100.0 * Math.sqrt(rmsModeAmp) ) / 100.0;
var modeNumber = [];
for (var ii = 0; ii < 88; ++ii) modeNumber[ii] = ii;
var bunchNumber = [];
for (var ii = 0; ii < 176; ++ii) bunchNumber[ii] = ii;
device = flow.get('device');

getAttrObject(device, 'readings', 'beamCurrent').value      =  beamCurrent;
getAttrObject(device, 'readings', 'maxDipoleMode').value    =  maxDipoleMode;
getAttrObject(device, 'readings', 'avgPhase').value         =  avgPhase;
getAttrObject(device, 'readings', 'maxDipoleModeAmp').value =  maxDipoleModeAmp;
getAttrObject(device, 'readings', 'rmsModeAmp').value       =  rmsModeAmp;
getAttrObject(device, 'readings', 'avgMag').value           =  msg.payload.avgMag;
getAttrObject(device, 'readings', 'phaseShiftQ').value      =  avgQ;
getAttrObject(device, 'readings', 'modeM').value            =  [modeNumber,modeM];
getAttrObject(device, 'readings', 'bunchCurrent').value     =  [bunchNumber,msg.payload.magn];
getAttrObject(device, 'readings', 'difPhase').value         =  [bunchNumber,msg.payload.difPhase];
getAttrObject(device, 'readings', 'phase').value            =  [bunchNumber,msg.payload.phase];
getAttrObject(device, 'readings', 'bunchLength').value      =  [bunchNumber,msg.payload.bunchLengthScale];
getAttrObject(device, 'readings', 'peakBunchCurrent').value =  [bunchNumber,msg.payload.peakBeamCurrent];
getAttrObject(device, 'readings', 'trace1').value =  [bunchNumber,msg.payload.trace1];
getAttrObject(device, 'readings', 'trace2').value =  [bunchNumber,msg.payload.trace2];
getAttrObject(device, 'readings', 'trace3').value =  [bunchNumber,msg.payload.trace3];
getAttrObject(device, 'readings', 'trace4').value =  [bunchNumber,msg.payload.trace4];
getAttrObject(device, 'readings', 'linkQuality').value =  flow.get('linkQuality');
getAttrObject(device, 'readings', 'signalLevel').value =  flow.get('signalLevel');
flow.set('device', device);
return {topic:'deviceReadings',payload:device};
